export default function AutoComponent(pattern=/.*/, base=Object) {
  return class AutoComponent extends base {
    constructor() {
      super()
      var cls = this.constructor
      var methods, methodName
      while (cls !== AutoComponent) {
        methods = Object.getOwnPropertyNames(cls.prototype)
        for (var i in methods) {
          methodName = methods[i]
          if (methodName.match(pattern) !== null) {
            this[methodName] = this[methodName].bind(this)
          }
        }
        cls = cls.__proto__
      }
    }
  }
}
